const slider=document.querySelector(".slider"),sliderItems=document.querySelectorAll(".slider-item"),btnPrev=document.querySelector("#btn-prev"),btnNext=document.querySelector("#btn-next");let counter=1,size=sliderItems[0].clientWidth;const resize=()=>{size=sliderItems[0].clientWidth};window.onresize=resize,slider.style.transform=`translateX(${-size*counter}px)`;const slide=e=>{slider.style.transition="transform 1.5s ease-in-out","right"===e?counter++:counter--,slider.style.transform=`translateX(${-size*counter}px)`};btnNext.addEventListener("click",()=>{counter>=sliderItems.length-1||slide("right")}),btnPrev.addEventListener("click",()=>{counter<=0||slide("left")}),slider.addEventListener("transitionend",()=>{"last-copy"===sliderItems[counter].getAttribute("id")&&(slider.style.transition="none",counter=sliderItems.length-2,slider.style.transform=`translateX(${-size*counter}px)`),"first-copy"===sliderItems[counter].getAttribute("id")&&(slider.style.transition="none",counter=sliderItems.length-counter,slider.style.transform=`translateX(${-size*counter}px)`)});let rotating=!0,sliderSpeed=4500,slideDirection="right";slider.addEventListener("mouseover",()=>{rotating=!1}),btnNext.addEventListener("mouseover",()=>{rotating=!1}),btnPrev.addEventListener("mouseover",()=>{rotating=!1}),slider.addEventListener("mouseout",()=>{rotating=!0});const infiniteSlider=setInterval(()=>{!rotating||slide(slideDirection)},sliderSpeed);